{
    "class": "AS3",
    "action": "deploy",
    "persist": true,
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.12.0",
        "id": "isc-lab",
        "label": "calalang_https",
        "remark": "HTTPS Application Service",
        "target": {
            "hostname": "calalang-azure-0.westus2.cloudapp.azure.com"
        },
        "calalang_https": {
            "class": "Tenant",
            "MyWebApp2https": {
                "class": "Application",
                "template": "https",
                "statsProfile": {
                    "class": "Analytics_Profile",
                    "collectedStatsInternalLogging": true,
                    "collectedStatsExternalLogging": true,
                    "capturedTrafficInternalLogging": true,
                    "capturedTrafficExternalLogging": true,
                    "collectPageLoadTime": true,
                    "collectClientSideStatistics": true,
                    "collectResponseCode": true,
                    "collectGeo": true,
                    "collectIp": true,
                    "collectMaxTpsAndThroughput": true,
                    "collectMethod": true,
                    "collectOsAndBrowser": true,
                    "collectUserAgent": true,
                    "collectUserSession": true,
                    "collectSubnet": true,
                    "collectUrl": true,
                    "sessionCookieSecurity": "ssl-only"
                },
                "serviceMain": {
                    "class": "Service_HTTPS",
                    "virtualAddresses": [
                        "10.0.2.14"
                    ],
                    "pool": "web_pool",
                    "profileAnalytics": {
                        "use": "statsProfile"
                    },
                    "serverTLS": "webtls"
                },
                "web_pool": {
                    "class": "Pool",
                    "monitors": [
                        "http"
                    ],
                    "members": [
                        {
                            "servicePort": 80,
                            "serverAddresses": [
                                "10.0.3.39"
                            ],
                            "shareNodes": true
                        }
                    ]
                },
                "webtls": {
                    "class": "TLS_Server",
                    "certificates": [
                        {
                            "certificate": "webcert"
                        }
                    ]
                },
                "webcert": {
                    "class": "Certificate",
                    "certificate": {
                        "bigip": "/Common/default.crt"
                    },
                    "privateKey": {
                        "bigip": "/Common/default.key"
                    }
                }
            }
        }
    }
}

