{
    "description": "Task 6 - Create custom HTTP AS3 Template on BIG-IQ",
    "name": "HTTPcustomTemplateTask6",
    "schemaOverlay": {
        "type": "object",
        "properties": {
            "class": {
                "type": "string",
                "const": "Application"
            },
            "schemaOverlay": {},
            "label": {},
            "remark": {},
            "template": {},
            "enable": {},
            "constants": {}
        },
        "additionalProperties": {
            "allOf": [
                {
                    "if": {
                        "properties": {
                            "class": {
                                "const": "Service_HTTP"
                            }
                        }
                    },
                    "then": {
                        "$ref": "#/definitions/Service_HTTP"
                    }
                }
            ],
            "not": {
                "anyOf": [
                    {
                        "properties": {
                            "class": {
                                "const": "IAM_Policy"
                            }
                        }
                    },
                    {
                        "properties": {
                            "class": {
                                "const": "WAF_Policy"
                            }
                        }
                    }
                ]
            }
        },
        "required": [
            "class"
        ],
        "definitions": {
            "Service_HTTP": {
                "type": "object",
                "properties": {
                    "virtualPort": {
                        "type": "integer",
                        "const": 8080,
                        "default": 8080
                    }
                },
                "dependencies": {
                    "policyIAM": {
                        "not": {}
                    },
                    "policyWAF": {
                        "not": {}
                    }
                },
                "additionalProperties": true
            }
        }
    }
}

